class CreateBeOneAdminRecords < ActiveRecord::Migration[6.0]

  def change
    [
      Admin::User,
      Admin::Menu,
      Admin::Permission,
      Admin::Role,
      Admin::Notification,
      Admin::Confirmation,
    ].each(&:delete_all)

    # Create permissions
    Admin.modules.each_pair do |_module, actions|
      Admin::Permission.create!( title: I18n.available_locales.inject({}){|hash,lang| hash[lang] = "#{_module} Manager";hash }, controller: _module, actions: actions, allowed: true  )
    end

    # Create role
    admin_role = Admin::Role.create!( title: I18n.available_locales.inject({}){|hash,lang| hash[lang] = "Super Admin";hash }, description: I18n.available_locales.inject({}){|hash,lang| hash[lang] = "Super Admin";hash } )
    admin_role.permissions = Admin::Permission.all

    # Create admin
    admin = Admin::User.create!( email: "admin@admin.net", first_name: "John", last_name: "Smith", title: "CEO", password: "admin", has_root_resource_scope: true, has_child_resource_scope:true, path:0 )
    admin.roles = Admin::Role.all

    admin.save
    Admin::UserAction.update_user_permissions admin

    # Create menu
    parent = nil
    Admin.modules.keys.each do |_module|
      name = _module.demodulize
      if name == _module
        g = {title: I18n.available_locales.inject({}){|hash,lang| hash[lang] = _module.demodulize; hash }, controller: _module , parent_id: 0 }
        parent = Admin::Menu.create!( title: I18n.available_locales.inject({}){|hash,lang| hash[lang] = _module.demodulize; hash }, controller: _module , parent_id: 0 )
      else
        Admin::Menu.create!( title: I18n.available_locales.inject({}){|hash,lang| hash[lang] = _module.demodulize; hash }, controller: _module , parent_id: parent.id )
      end
    end


  

